const Addexpense = () => {
  const navigate = useNavigate();

  const handleAddExpense = async () => {
    // Gather data from input fields
    const category = document.querySelector('input[name="category"]').value;
    const date = document.querySelector('input[name="date"]').value;
    const amount = document.querySelector('input[name="amount"]').value;
    const description = document.querySelector('input[name="description"]').value;

    // Create payload object
    const expenseData = {
      category,
      date,
      amount,
      description
    };

    try {
      // Send expense data to backend
      const response = await axios.post('http://localhost:9000/addExpense', expenseData);
      console.log('Expense added:', response.data);
      // Redirect to a new page (or navigate to another route)
      navigate('/expenses');
    } catch (error) {
      console.error('Error adding expense:', error);
      // Handle error (e.g., display error message)
    }
  };

  return (
    <div className='add-container'>
      <div className="add-inputs">
        <input type="text" name="category" placeholder='Enter category of expense' />
        <input type="date" name="date" placeholder='Enter Date of expense' />
        <input type="text" name="amount" placeholder='Enter amount of expense' />
        <input type="text" name="description" placeholder='Enter description of expense' />
        <div className="submit" onClick={handleAddExpense}>
          Submit this expense
        </div>
      </div>
    </div>
  );
};

export default Addexpense;
