        <input type="date" name="date" placeholder='Enter Date of expense' />
        <input type="text" name="amount" placeholder='Enter amount of expense' />
        <input type="text" name="description" placeholder='Enter description of expense' />
        <div className="submit" onClick={handleAddExpense}>
          <div className="add-text">Submit this expense</div>
        </div>
      </div>
    </div>
    </div.maincontainer>
  );
};

export default Addexpense;
